CustomField.formula:
OR( 
AND( 
MOD(DATEVALUE(RequestDateTime) - DATE(2000, 1, 1), 7) = 0, 
Requested_Hour__c < 5 
), 
AND( 
MOD(DATEVALUE(RequestDateTime) - DATE(2000, 1, 1), 7) = 1, 
OR( 
Requested_Hour__c < 6, 
Requested_Hour__c > 17 
) 
), 
AND( 
MOD(DATEVALUE(RequestDateTime) - DATE(2000, 1, 1), 7) > 2, 
OR( 
Requested_Hour__c < 7, 
Requested_Hour__c > 17 
) 
) 
)
