ValidationRule.errorConditionFormula:
AND( 
OR( 
AND(ISCHANGED(Credit_Decision__c),TEXT(Credit_Decision__c)!= null) 
), 
OR( 
TEXT(Sanctions_Check__c) == 'Passed', 
TEXT(Compliance_Decision__c) == 'Approved' 
), 
OR( 
TEXT(Fraud_Recommendation__c) == 'Recommend Approve', 
TEXT(Fraud_Decision__c) == 'Approved' 
) 
)
