WebLink.url:
//Get the Salesforce apex connection libraries
{!REQUIRESCRIPT("/soap/ajax/30.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/30.0/apex.js")}
timeout=120000;
var ids = {!GETRECORDIDS($ObjectType.Campaign_Pricing__c)};
var interval;

//The main function that does all the work
function appendScript()
{
    //We want to use jQuery as well as the UI elements, so first lets load the       stylesheet by injecting it into the dom.
    var head= document.getElementsByTagName('head')[0];
    var v_css  = document.createElement('link');
    v_css.rel = 'stylesheet'
    v_css.type = 'text/css';
    v_css.href = 'https://ajax.googleapis.com/ajax/libs/jqueryui/1.8.16/themes/redmond/jquery-ui.css';
    head.appendChild(v_css);

    var script= document.createElement('script');
    script.type= 'text/javascript';
    script.src= 'https://ajax.googleapis.com/ajax/libs/jquery/1.5.2/jquery.min.js';
    head.appendChild(script);

    //And of course we need the jQuery UI elements
    var scriptUI = document.createElement('script');
    scriptUI.type = 'text/javascript';
    scriptUI.src = 'https://ajax.googleapis.com/ajax/libs/jqueryui/1.8.16/jquery-ui.js';   
    head.appendChild(scriptUI);

    interval=self.setInterval(function(){

        //Check to see if jQuery has loaded                           
        if(jQuery)
        {
            //if jQuery has loaded, clear the interval
            window.clearInterval(interval);

            //create a div in the body which we will use for our dialog.
            $(document.getElementsByTagName('body')[0]).append("<div id=infoNotice title='Updating Records'>Updating Records Please Wait...<div id='infoNoticeResult'>This will take a moment.<br /><progress max='100'></progress></div></div>");    

            //register our newly created div as a dialog
            $( "#infoNotice" ).dialog();   

            //setTimeout 

            setTimeout(function(){
      //get the ids to be refreshed

                var callResult = sforce.apex.execute("CampaignPricingController","RefreshCampaignPricingDataForCampaignProgram",{ID: ids});             

                $( "#infoNoticeResult" ).text(callResult[0]);
                 window.location.reload();

            },10);
        }
    }, 300);    
}
appendScript();
